const criticalKeys = [
    "location",       
    "travelerGroup",  
    "ageRange",
    "dietaryPrefs",
    "travelStyle",
    "budgetRange",
    "user_id"
];

for (const item of items) {
    const missingKeys = [];
    const inputData = item.json.body; 

    if (!inputData) {
        item.json.validationSucceeded = false;
        item.json.missingKeys = criticalKeys;
        return items;
    }

    for (const key of criticalKeys) {
        if (!(key in inputData) || inputData[key] === null || inputData[key] === "") {
            missingKeys.push(key);
        }
    }

    item.json.missingKeys = missingKeys;

    item.json.validationSucceeded = missingKeys.length === 0;
}

return items;

const searchTypes = [
    "Cafe",
    "Resturant"
];

const itemsToReturn = [];
const originalItem = items[0].json;
const queryData = originalItem.body || {};

const scarpeInvokeID = Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 15);

const userID = originalItem.body.user_id;

for (const type of searchTypes) {

    itemsToReturn.push({
        json: {
            ...originalItem, 
            ...queryData, 
            Type: type,
            ScarpeInvokeID: scarpeInvokeID,
            Status: 'Pending',
            UserID: userID
        }
    });
}

return itemsToReturn;

